mapper.select:
select

,

from activity


    activity_name = #{activityName,jdbcType=VARCHAR}


   AND create_name = #{createName,jdbcType=VARCHAR}


  AND  create_id = #{createId,jdbcType=BIGINT}


   AND activity_time >= #{startTime} AND activity_time < #{endTime}


   AND activity_long = #{activityLong,jdbcType=VARCHAR}


  AND  activity_where = #{activityWhere,jdbcType=VARCHAR}


  AND  all_person = #{allPerson,jdbcType=INTEGER}


  AND  join_person = #{joinPerson,jdbcType=INTEGER}


  AND  status = #{status,jdbcType=CHAR},


  AND   message = #{message,jdbcType=LONGVARCHAR}
