mapper.select:
select

from physical_examination


    id = #{id,jdbcType=BIGINT}


  AND  pid = #{pid,jdbcType=BIGINT}


  AND  name = #{name,jdbcType=VARCHAR}


  AND  diastolic_pressure = #{diastolicPressure,jdbcType=VARCHAR}


  AND  systolic_pressure = #{systolicPressure,jdbcType=VARCHAR}


    AND body_weight = #{bodyWeight,jdbcType=VARCHAR}


    AND waist_hip = #{waistHip,jdbcType=VARCHAR}


    AND urea = #{urea,jdbcType=VARCHAR}


    AND uric_acid = #{uricAcid,jdbcType=VARCHAR}


    AND "glycerol glycol" = #{glycerolGlycol,jdbcType=VARCHAR}


    AND cholesterol = #{cholesterol,jdbcType=VARCHAR}


    AND glucose = #{glucose,jdbcType=VARCHAR}


    AND protein = #{protein,jdbcType=VARCHAR}


    AND metrics = #{metrics,jdbcType=VARCHAR}


    AND record_time = #{recordTime,jdbcType=TIMESTAMP}


    AND record_time >= #{startTime} AND record_time < #{endTime}
